definePageMeta({ layout: 'app' })

export default defineComponent({
  name: 'BillingPage',
  data() {
    return {
      usage: {
        period_start: '',
        period_end: '',
        compute_gpu_hours: 24.5,
        storage_gb_months: 120,
        total_usd: 42.5,
        forecast_usd: 55,
      },
      invoices: [] as Array<{ id: string; amount_usd: number; status: string }>,
      loading: true,
      settingUp: false,
      stripeConnected: false,
    }
  },
