      return this.credentials.filter((c) => c.type === this.form.sourceType)
    },
    hfCredentials(): Credential[] {
      return this.credentials.filter((c) => c.type === 'hf')
    },
  },
  setup() {
    const { apiFetch } = useAployApi()
    return { apiFetch }
  },
  async mounted() {
    try {
