<script lang="ts">
import { defineComponent } from 'vue'

definePageMeta({ layout: 'app' })

interface TriggerItem {
  id: string
  pipeline_id: string
  name: string
  type: string
  status: string
  webhook_url?: string | null
}

interface PipelineItem { id: string; name: string }

